(function($) {
	$.fn.jdclear = function() {
		
		return this.each(function() {
			clearinit($(this));
		});
	}
	
	function clearinit(jdom) {
		$("input,select,radio,textarea",jdom).each(function() {
			var default_value = $(this).attr("defalut");
			if(default_value) {
				$(this).val(default_value);
			} else {
				$(this).val("");
			}
		});
	}
	
	$.fn.jdset = function(row) {
		return this.each(function() {
			setinit($(this), row);
		});
		
	}
	
	function setinit(that, row) {
		if(row) {
			for(p in row) {
				if(row.hasOwnProperty(p)) {
					$("#"+p, that).val(row[p]);
				}
			}
		}
	}
})(jQuery);