/** easyUI Tree*/
(function($) {
	/**async Tree */
	$.fn.asyncTree = function(options) {
		var setting = $.extend({},$.fn.jdshowFormAjax.defaults, options);
		return this.each(function() {
			$(this).tree(setting);
		});
	}
	
	$.fn.asyncTree.defaults = {
		url: null,
		method:"get",
		checkbox:true,
		animate:true,
		lines:true,
		queryParams:{id: 0}
	}
})(jQuery)